// Challenge / Exercise

// 1. Add five new (dummy) page components (content can be simple <h1> elements)
//    - HomePage x
//    - EventsPage x
//    - EventDetailPage x
//    - NewEventPage x
//    - EditEventPage x
// 2. Add routing & route definitions for these five pages
//    - / => HomePage x
//    - /events => EventsPage x
//    - /events/<some-id> => EventDetailPage x
//    - /events/new => NewEventPage x
//    - /events/<some-id>/edit => EditEventPage x
// 3. Add a root layout that adds the <MainNavigation> component above all page components x
// 4. Add properly working links to the MainNavigation x
// 5. Ensure that the links in MainNavigation receive an "active" class when active x
// 6. Output a list of dummy events to the EventsPage
//    Every list item should include a link to the respective EventDetailPage
// 7. Output the ID of the selected event on the EventDetailPage
// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import Homepage from "./pages/Homepage";
import EventsPage from "./pages/EventsPage";
import EventDetailsPage from "./pages/EventDetailPage";
import NewEventPage from "./pages/NewEventPage";
import EditEventPage from "./pages/EditEventPage";
import RootLayout from "./pages/Root";

const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout />,
    children: [
      {
        index: true,
        element: <Homepage />,
      },
      {
        path: "/events",
        element: <EventsPage />,
      },
      {
        path: "/events/<some-id>",
        element: <EventDetailsPage />,
      },
      { path: "/events/new", element: <NewEventPage /> },
      {
        path: "/events/<some-id>/edit",
        element: <EditEventPage />,
      },
    ],
  },
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
